import React, { Component } from 'react';
import './css/simulation.css'


class App extends Component {

    constructor(props) {
        super(props);
        this.simModel = new window.simcapi.CapiAdapter.CapiModel({
            demoMode: true,
            authorMode: false,
            teacherMode: false,
            simEnabled: true,
            redCedar: -1,
            redHawk: -1,
            snowHare: 0,
            bigSage: 0
        });


        this.state = {
            demoMode: true,
            authorMode: false,
            teacherMode: false,
            simEnabled: true,
            redCedar: 0,
            redHawk: 0,
            snowHare: 0,
            bigSage: 0
        }

        window.simcapi.CapiAdapter.expose('redCedar', this.simModel);
        window.simcapi.CapiAdapter.expose('redHawk', this.simModel);

        window.simcapi.Transporter.notifyOnReady();

        this.increment = this.increment.bind(this)


    }


    increment(objectName) {
        this.setState({
            [objectName]: this.state[objectName] + 1
        });
        console.log(this.state);
        this.simModel.set(objectName, this.state[objectName]);
    }




    render() {
    return (
      <div className="App">
          <ul>
              <li className="dropdown1">
                  <a href="javascript:void(1)" className="dropbtn2">Add Predators </a>
                  <div className="dropdown-content1">
                    <a onClick={(e) => this.increment('redCedar')}>Western Red Cedar</a>
                  </div>
              </li>
              <li className="dropdown2">
                  <a href="javascript:void(1)" className="dropbtn2">Add Predators </a>
                  <div className="dropdown-content2">
                      <a onClick={(e) => this.increment('redHawk')} href="#">Red Tailed Hawk</a>
                  </div>
              </li>
              <li className="dropdown3">
                  <a href="javascript:void(2)" className="dropbtn3">Add Prey</a>
                  <div className="dropdown-content3">
                      <a onClick={(e) => this.increment('snowHare')} href="#">Snowshoe Hare</a>
                  </div>
              </li>
              <li className="dropdown4">
                  <a href="javascript:void(2)" className="dropbtn4">Add Bushes</a>
                  <div className="dropdown-content4">
                      <a onClick={(e) => this.increment('bigSage')} href="#">Big Sagebrush</a>
                  </div>
              </li>
          </ul>

          <br/>
          <br/>


          <div>
              Western Red Cedar: {this.state['redCedar']} <br/>
              Red Tailed Hawk: {this.state['redHawk']}
          </div>

      </div>
    );
  }
}

export default App;
