import React, { Component } from 'react';
import './css/simulation.css'


class App extends Component {

    constructor(props) {
        super(props);
        this.state = {
            demoMode: true,
            authorMode: false,
            teacherMode: false,
            simEnabled: true,
            redCedar: 0,
            redHawk: 0,
            snowHare: 0,
            bigSage: 0
        };

        this.simModel = new window.simcapi.CapiAdapter.CapiModel(this.state);

        for (var key in this.state) {
            if (!this.state.hasOwnProperty(key)) continue;
            this.addListener(key)
            window.simcapi.CapiAdapter.expose(key, this.simModel);
        }



        window.simcapi.Transporter.notifyOnReady();

        this.increment = this.increment.bind(this)


    }


    addListener(keyName) {
        this.simModel.on( ('change:' + keyName), () => {
            console.log('Update ' + keyName);
            console.log(this.state);
            this.setState(
                {
                    [keyName]: this.simModel.get(keyName)
                });
        });
    }

    increment(objectName) {
        var count = this.simModel.get(objectName);
        console.log(objectName, count + 1);
        this.simModel.set(objectName, count + 1);
    }


    render() {
    return (
      <div className="App">
          <ul>
              <li className="dropdown1">
                  <a href="javascript:void(1)" className="dropbtn2">Add Predators </a>
                  <div className="dropdown-content1">
                    <a onClick={(e) => this.increment('redCedar')} href="#">Western Red Cedar</a>
                  </div>
              </li>
              <li className="dropdown2">
                  <a href="javascript:void(1)" className="dropbtn2">Add Predators </a>
                  <div className="dropdown-content2">
                      <a onClick={(e) => this.increment('redHawk')} href="#">Red Tailed Hawk</a>
                  </div>
              </li>
              <li className="dropdown3">
                  <a href="javascript:void(2)" className="dropbtn3">Add Prey</a>
                  <div className="dropdown-content3">
                      <a onClick={(e) => this.increment('snowHare')} href="#">Snowshoe Hare</a>
                  </div>
              </li>
              <li className="dropdown4">
                  <a href="javascript:void(2)" className="dropbtn4">Add Bushes</a>
                  <div className="dropdown-content4">
                      <a onClick={(e) => this.increment('bigSage')} href="#">Big Sagebrush</a>
                  </div>
              </li>
          </ul>


      </div>
    );
  }
}

export default App;
